from os import pathimport osimport subprocessEMULATOR_PATH = path.abspath("emulator\\bgb\\bgb.exe")GBDK_BIN_PATH = "D:\\gbdk\\bin"LCC = path.join(path.abspath(GBDK_BIN_PATH), "lcc")PROJECT_FOLDER = "..\\game-src"OUTPUT_FOLDER = "..\\game-builds"C_FILES = tuple(map(    lambda x: path.join(PROJECT_FOLDER, x),    ["main.c"]))assert("main.c" in C_FILES, "There MUST be a main.c file (an entry point)")build_command = f"{LCC} -o "for c_file in C_FILES:    gb_file = path.basename(c_file).split('.')[0]    gb_file = f"{path.join(OUTPUT_FOLDER, gb_file)}.gb"    build_command += f"{gb_file} {c_file}"print(f"Build command: {build_command}")# Step 1. Buildprint(f"Build exited with: {subprocess.call(build_command, shell=True)}")# Step 2. Runmain_file = path.join(OUTPUT_FOLDER, "main.gb")run_command = f"\"{EMULATOR_PATH}\" -rom {main_file}"print(f"Emulator exited with: {subprocess.call(run_command, shell=True)}")